package com.syntaxClass09;

public class PatternsMore {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Decrement
		for(int r=5; r>=1; r--) {// control rows
			for(int c=4; c>=1; c--) {//control columns
				
				System.out.print(c+" ");
			}
			System.out.println();
			
		}
		System.out.println("----------------");
		for(int r=5; r>=1; r--) {// control rows
			for(int c=4; c>=1; c--) {//control columns
				
				System.out.print(r+" ");
			}
			System.out.println();
		}
		
		
		
	}

}
