package com.syntaxClass3;

public class Recap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String var1="Hello world";
		int number1=10;
		String result=var1+number1;
		System.out.println(result);
		boolean isTrue=true;
		System.out.println(isTrue+result);
		System.out.println("***************************");
		
		int number2=14;
		int number3=3;
		System.out.println(number2/number3);
		System.out.println(number2%number3);
		System.out.println("**************************");
		
		double number4=14;
		double number5=3;
		System.out.println(number4/number5);
		System.out.println(number4%number5);
		System.out.println("****************************");
		
		int var2=-100;
		var2++;
		System.out.println(var2);
		var2--;
		System.out.println(var2);
		// number** not allowed because it does not change anything
		// number// not allowed because it does not change anything
		System.out.println("***********************************");
		
		double number6=10;
		number6++;
		System.out.println(number6);
		System.out.println("***********************************");
		
		double number7=20;
		number7+=10;
		System.out.println(number7);
		number7-=40;
		System.out.println(number7);
		System.out.println("*************************************");
		
		int number8=100;
		number8*=4;
		System.out.println(number8);
			
				
		
		

	}

}
