package com.syntax.class02;

public class ArithmaticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number=10;
		int number2=20;		
		System.out.println(number+number2);
		System.out.println(number-number2);
		System.out.println(number*number2);
		System.out.println(number/number2);
		
		double number3=10;
		double number4=20;
		System.out.println(number3+number4);
		System.out.println(number3-number4);
		System.out.println(number3*number4);
		System.out.println(number3/number4);
		
		boolean var1=true;
		boolean var2=false;
		//System.out.println(var1+var2); Not allowed on booleans
		
		char char1='A';
		char char2='B';
		System.out.println(char1+char2);
		System.out.println(char1-char2);
		System.out.println(char1*char2);
		System.out.println(char1/char2);
		
		String str1="hi";
		String str2="bye";
		System.out.println(str1+str2);
		//System.out.println(str1-str2); Subtraction Not Allowed
		//System.out.println(str1*str2); Multiply Not Allowed
		//System.out.println(str1/str2); Division Not Allowed
				
	}

}
